css:
h1 {
    text-align: center;
}

h2 {
    margin-top: 0px !important; 
    margin-bottom: 10px !important; 
    font-size: 9;
    margin:0px;
    text-align: center;
}

hr {
    height: 1px;
    background-color: #000; 
    margin: 5px 0;
}

table {
    border-collapse: collapse;
}

th, td {
    border: 1px solid #ccc; /* Adiciona borda às células */
    line-height: 0; /* Reduz o espaçamento entre linhas */
}

tbody tr:nth-child(odd) {
    background-color: #f9f9f9;
}

tbody tr:nth-child(even) {
    background-color: #e6e6e6;
}

.titulo {
    text-align: center;
    font-size: 13;

}

.marca {
    font-size: 14px;
    text-align: center;
}
.cabecalho {
    font-size: 12px;
    justify-content: space-between; /* Espaço entre os elementos */
}

.cod_pedido {
    text-align: right; 
}
.tabela_artigos thread{
    font-size: 12px;
}

.tabela_artigos tbody{
    font-size: 11px;
}

.tabela_totais{
    margin-left: 0;
}

.tabela_totais thread{
    font-size: 12px;
}

.tabela_totais tbody{
    font-size: 10px;
}

.tabela_desconto thread{
    font-size: 14px;
}

.tabela_desconto tbody{
    font-size: 13px;
}

.tabela_desconto th, td {
    border: 1px solid #ccc; /* Adiciona borda às células */
    line-height: 0.5; /* Reduz o espaçamento entre linhas */
}
.promessa_devolucao { 
    font-size:12px;
}
